import React, { Component, Fragment } from 'react'
import {Route, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';



export class PrivateAdminRoute extends Component {
    constructor(){
        super();
        this.state = {
            fullname: ''
        }
    }

    UNSAFE_componentWillReceiveProps(nextProps){
        if(nextProps) {
            console.log("Next props",nextProps)
            this.setState({ fullname: nextProps.adminFullname})
        }
    }

    render() {
        const { authenticated, component, adminFullname, computedMatch :{params : { fullname }} } = this.props;
        //fix authentication with fullname && id
        // let adminAuthentication = (fullname == adminFullname);
        console.log("This is trueee",this.state.fullname)
        // console.log("provate route", fullname, props);

        let adminRoute = (this.state.fullname === fullname) ? (
            <Route component={component} />
        ) : (
            <Redirect to='/' />
        )

        return (adminRoute)
    }
}

PrivateAdminRoute.propTypes = {
    authenticated: PropTypes.bool.isRequired,
    adminFullname: PropTypes.string.isRequired
}


const mapStateToProps = (state) => ({
    admin: state.admin,
    authenticated: state.admin.authenticated,
    adminFullname: state.admin.fullname

});


export default connect(mapStateToProps)(PrivateAdminRoute);
