import React, { Component, Fragment } from 'react'
import {Route, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';



export class PrivateAdminRoute extends Component {
    constructor(){
        super();
        this.state = {
            fullname: '',
            pathAdminName:''
        }
    }

    componentDidUpdate(prevProps) {
        console.log("Private Next props",this.prevProps)
        if (this.props.adminFullname !== prevProps.adminFullname) {

            this.setState({ 
                fullname: this.props.adminFullname,
                pathAdminName: this.props.computedMatch.params.fullname
            })
        }

    }

    render() {
        const { authenticated, component} = this.props;
        //fix authentication with fullname && id
        let adminAuthentication = (authenticated && (this.state.fullname === this.state.pathAdminName));
        console.log("This is trueee",this.state.fullname, this.state.pathAdminName)
        // console.log("provate route", fullname, props);

        let adminRoute = (adminAuthentication) ? (
            <Route component={component} />
        ) : (
            <Redirect to='/' />
        )

        return (adminRoute)
    }
}

PrivateAdminRoute.propTypes = {
    authenticated: PropTypes.bool.isRequired,
    adminFullname: PropTypes.string.isRequired
}


const mapStateToProps = (state) => ({
    admin: state.admin,
    authenticated: state.admin.authenticated,
    adminFullname: state.admin.fullname

});


export default connect(mapStateToProps)(PrivateAdminRoute);
