import React, { Component } from 'react';
import withStyles from '@material-ui/core/styles/withStyles';
import PropTypes from 'prop-types';

// import AppIcon from '../images/ori_logo.png';
import Typography from '@material-ui/core/Typography';
import noImg from '../../../images/no-pic.png';
import { Bounce, Shake, FadeIn } from 'react-motions' //https://github.com/raphamorim/react-motions
import appLogo from '../../../images/ori_logo.png';

//mui studd
import Grid from '@material-ui/core/Grid'
import Slide from '@material-ui/core/Slide';
import Button from '@material-ui/core/Button';
import CircularProgress from '@material-ui/core/CircularProgress';
import PersonOutlineIcon from '@material-ui/icons/PersonOutline';// import { connect } from 'react-redux';
// import { signupUser } from '../redux/actions/userActions'

const Link = require("react-router-dom").Link

const buttonStyles = {
    width: "200px",
    bottom: "45px",
    marginLeft: "50px",
    color: "white",
    borderRadius: "20px"
    }

const styles = {
    pageTitle: {
        marginTop: "-10px",
        textAlign: "center"
    },
    container: {
        marginTop: "-10px",
    },
    personIcon: {
        height: "100%",
        width: "100%",
        color: "lightseagreen",
    },
    adminBtn: {
        ...buttonStyles,
        backgroundColor: "darkblue",
    },
    userBtn: {
        ...buttonStyles,
        backgroundColor: "darkmagenta",
    },
    clientBtn: {
        ...buttonStyles,
        backgroundColor: "darkgoldenrod",
    },
    onBtnHover: {
        ...buttonStyles,
        backgroundColor: "yellow",
        color: "black"
    },
    btnDetails: {
        width: "200px",
        marginLeft: "50px",
        marginTop: "-30px"
    },
    logoContainer: {
        marginTop: '80px',
        textAlign: "center"
    }
}


class registration extends Component {


    state = {
        hover: false,
        free: true
    }


    toggleHover = () =>  { 
        this.setState({hover: true}); 
    }
    toggleLeave = () =>  { this.setState({hover: false, free: false}); }


    render() {
        const  {hover, free } = this.state;

        return (
            <FadeIn duration={4}>
                <Grid container>
                    <Grid item xs={12}>
                        <div style={styles.logoContainer}>
                            <img src={appLogo} alt="icon" style={{width: 250}}/>
                        </div>

                        <Typography variant="h4" style={styles.pageTitle} >
                            CHOOSE THE ACCOUNT THAT FIT YOUR INTEREST
                        </Typography>

                        <Grid container justify="center" style={styles.container} >
                            <Grid item>
                                <Grid item>
                                    <PersonOutlineIcon style={styles.personIcon} />
                                </Grid>
                                <Grid item>
                                    <Button 
                                    style={!hover && free? styles.adminBtn: styles.onBtnHover } 
                                    onMouseEnter={this.toggleHover} 
                                    onMouseLeave={this.toggleLeave}
                                    variant="contained" 
                                    component={Link} to="/admin-register" >
                                        Admin-Registration
                                    </Button>
                                    <Typography style={styles.btnDetails} variant="body2" color="textSecondary" component="p">
                                        Register as an administrator of an organization. This account contains elevated
                                        privileges for monitoring, managing, and modifying the system and its participants at any
                                        level.
                                    </Typography>
                                </Grid>
                            </Grid>
                            <Grid item>
                                <Grid item>
                                    <PersonOutlineIcon style={styles.personIcon} />
                                </Grid>
                                <Grid item>
                                    <Button 
                                    style={!hover && free ? styles.userBtn: styles.onBtnHover } 
                                    onMouseEnter={this.toggleHover} 
                                    onMouseLeave={this.toggleLeave}
                                    variant="contained" 
                                    component={Link} 
                                    to="/user-register" >
                                        User-Registration
                                    </Button>
                                    <Typography style={styles.btnDetails} variant="body2" color="textSecondary" component="p">
                                        Register as a user of an organization. This account is managed by the user and its 
                                        administrators. Users can access the clock-in-out system, view additional information,
                                        and new resources.
                                    </Typography>                        
                                </Grid>
                            </Grid>
                            <Grid item>
                                <Grid item>
                                    <PersonOutlineIcon style={styles.personIcon} />
                                </Grid>
                                <Grid item>
                                    <Button 
                                    style={!hover && free ? styles.clientBtn: styles.onBtnHover } 
                                    onMouseEnter={this.toggleHover} 
                                    onMouseLeave={this.toggleLeave}                                    variant="contained" 
                                    component={Link} 
                                    to="/client-register" >
                                        Client-Registration
                                    </Button>
                                    <Typography style={styles.btnDetails} variant="body2" color="textSecondary" component="p">
                                        Register as a client of an organization. This account is managed by the 
                                        administrators. Clients can only access the clock-in-out system.
                                    </Typography>                        
                                </Grid>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </FadeIn>
        )
    }
}


export default registration;
