import React, { Component } from 'react';
// import withStyles from '@material-ui/core/styles/withStyles';
import PropTypes from 'prop-types';
// import AppIcon from '../images/ori_logo.png';
import Typography from '@material-ui/core/Typography';
import styles from './styles';


//mui studd
import Grid from '@material-ui/core/Grid'
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import CircularProgress from '@material-ui/core/CircularProgress';
// import { ThemeProvider as MuiThemeProvider } from '@material-ui/core/styles';

//Redux stuf
import { connect } from 'react-redux';
import { loginFunc } from '../redux/actions/userActions'



const Link = require("react-router-dom").Link


class login extends Component {

    constructor(){
        super();
        this.state = {
            email: '',
            password: '',
            errors: []
        }
    }

    // UNSAFE_componentWillReceiveProps(nextProps){
    //     if(nextProps.UI.errors) {
    //         this.setState({ errors: nextProps.UI.errors })
    //     }
    // }

    // handleSubmit = (event) => {

    //     event.preventDefault();
    //     const userData = {
    //         email: this.state.email,
    //         password: this.state.password
    //     }
    //     this.props.loginFunc(userData, this.props.history);
    // }

    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    render() {
        // const { classes, UI: { loading } } = this.props;
        // const errors = this.state.errors.errors ? this.state.errors.errors : this.state.errors;
        // let { errors } = this.state;


        // console.log(errors);
        return (
            <Grid container >
                <Grid item sm />
                <Grid item sm>
                    {/* <img src={AppIcon} alt="icon" className={image}/> */}
                    <Typography variant="h3" >
                        Login
                    </Typography>
                    <form noValidate onSubmit={this.handleSubmit} >
                        <TextField
                        id='email'
                        name='email'
                        type="email"
                        label="Email"
                        style={styles.textField}
                        helperText={''}
                        error={false}
                        value={this.state.email}
                        onChange={this.handleChange}
                        fullWidth />

                        <TextField
                        id='password'
                        name='password'
                        type="password"
                        label="Password"
                        style={styles.textField}
                        helperText={''}
                        error={false}
                        value={this.state.password}
                        onChange={this.handleChange}
                        fullWidth />

                      {/* {errors.general && (
                            <Typography variant="body2" className={}>
                                {}
                            </Typography>
                        )} */}

                        <Button
                        type="submit"
                        variant="contained"
                        color="primary"
                        // className={button}
                        // disabled={false}
                        > Login
                        {/* {loading && (
                            <CircularProgress size={30} className={classes.progress} />
                        )} */}
                        </Button>
                        <br/>
                        <small>
                            Do not have an account!! Don't wait Sign Up <Link to="/register">Here</Link>
                        </small>
                    </form>
                </Grid>
                <Grid item sm/>

            </Grid>

        )
    }
}


login.propTypes = {
    classes: PropTypes.object.isRequired,
    loginFunc: PropTypes.func.isRequired,
    user: PropTypes.object.isRequired,
    UI: PropTypes.object.isRequired
}

const mapStateToProps = (state) => ({
    user: state.user,
    UI: state.UI
});

const mapActionsToProps = {
    loginFunc
}

export default connect(mapStateToProps, mapActionsToProps)(styles)(login);
