import React, { Component } from 'react';
import PropTypes from 'prop-types';
// import AppIcon from '../images/ori_logo.png';
import Typography from '@material-ui/core/Typography';
import appLogo from '../../../../images/ori_logo.png';
import styles from './styles';


//mui studd
import Grid from '@material-ui/core/Grid'
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import CircularProgress from '@material-ui/core/CircularProgress';
import MuiPhoneNumber from "material-ui-phone-number";
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import PanToolIcon from '@material-ui/icons/PanTool';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Switch from '@material-ui/core/Switch';

//Redux stuf
import { connect } from 'react-redux';
import { organizationAction } from '../../../../redux/actions/adminActions'

const Link = require("react-router-dom").Link


class organizationForm extends Component {

    constructor(){
        super();
        this.state = {
            firstN: '',
            lastN: '',
            email: '',
            password: '',
            confirmPassword: '',
            dob: '',
            phone: '',
            organization: '',
            orgExist: false,
            errors: {}
        }
    }

    UNSAFE_componentWillReceiveProps(nextProps){
        if(nextProps.UI.errors) {
            this.setState({ errors: nextProps.UI.errors })
        }
    }

    handleSubmit = (event) => {

        event.preventDefault();

        this.setState({
            loading: true
        });

        const newUserData = {
            email: this.state.email,
            password: this.state.password,
            confirmPassword: this.state.confirmPassword,
            handle: this.state.handle
        }

    //    this.props.signupUser(newUserData, this.props.history);
    }

    handleChange = (event) => {
        console.log(event.target.name)
        if (event.target.name === "orgExist"){
            this.setState({
                [event.target.name]: !this.state.orgExist
            })
        } else {
            this.setState({
                [event.target.name]: event.target.value
            })
        }
    }

    handlePhoneChange = (value) => {
        if (value) {
          this.setState({ phone: value });
        }
      }

    render() {

        // const { styles, UI: { loading } } = this.props;
        // const errors = this.state.errors.errors ? this.state.errors.errors : this.state.errors;
        // console.log(errors);
        return (
            <Grid >
                <Grid container>
                    <Grid item sm />
                    <Grid item sm>
                        <form noValidate onSubmit={this.handleSubmit} style={styles.form}>

                        <Typography variant="h6" style={{margin: "20px auto 10px auto"}}>
                            ORGANIZATION
                        </Typography>
                        <div>
                            <Typography variant="subtitle1">
                                Is your organization register with us?
                            </Typography>
                            {/* <FormControlLabel label="No" /> */}
                            <Grid component="label" container alignItems="center" spacing={1}>
                                <Grid item>No</Grid>
                                    <Grid item>
                                        <Switch
                                            name="orgExist"
                                            checked={this.state.orgExist}
                                            onChange={this.handleChange}
                                            value={this.state.orgExist}
                                        />
                                    </Grid>
                                <Grid item>Yes</Grid>
                            </Grid>



                            {/* <FormControl>
                                <Select
                                    labelId="demo-simple-select-label"
                                    id="demo-simple-select-outlined"
                                    value={this.state.orgExist}
                                    onChange={this.handleChange}
                                    name="orgExist"
                                    style={{margin: "10px 20px"}}
                                    >
                                    <MenuItem value={true}>Yes</MenuItem>
                                    <MenuItem value={false}>No</MenuItem>
                                </Select>
                            </FormControl> */}
                    </div>

                    {this.state.orgExist ? (
                        <Typography variant="body2">
                        An email has been sent to other admins, please verify
                        </Typography>
                    ) : (
                        <Typography variant="body2">
                            Will you like to register this organization?
                        </Typography>
                    )}

                    {/* {errors.general && (
                        <Typography variant="body2" style={styles.customError}>
                            {errors.general}
                        </Typography>
                    )} */}
                    </form>
                </Grid>
                <Grid item sm/>

            </Grid>
        </Grid>
        )

    }
}


organizationForm.propTypes = {
    admin: PropTypes.object.isRequired,
    UI: PropTypes.object.isRequired,
    organizationAction: PropTypes.func.isRequired
}

const mapStateToProps = (state) => ({
    admin: state.admin,
    UI: state.UI
});

export default connect(mapStateToProps, { organizationAction })(organizationForm);
